import { useState } from "react"
import { apiKeys } from "../data/apiKeys"
import { useEffect } from "react"
import { useParams } from "react-router"

function Product() {

    const { id } = useParams()
   
    const [product, setProduct] = useState([])
    const [productArticle, setProductArticle] = useState(`${id}`)

    useEffect(() => {
        fetch("    https://apidojo-hm-hennes-mauritz-v1.p.rapidapi.com/products/detail?lang=en&productcode=" + id + "&country=asia2", {
            "method": "GET",
            "headers": {
                "x-rapidapi-key": `${apiKeys[0].api_key}`,
                "x-rapidapi-host": "apidojo-hm-hennes-mauritz-v1.p.rapidapi.com"
            }
        })
            .then(response => response.json())
            .then(data => {
                setProduct(data.product)
                console.log(data.product);
            })
    }, [id])

    return(
        <section className="product">
            <div className="container">
                <div className="product-inner">
                    <div className="product-left">
                        <div className="product-gallery">
                            {
                                product.articlesList ? product.articlesList.filter(item => (
                                    item.code === productArticle
                                )).galleryDetails.map((galleryDetail) => (
                                    <div className="product-gallery-img">
                                        <img src={galleryDetail.url} alt="" />
                                    </div>
                                )) : null
                            }
                        </div>
                    </div>
                    <div className="product-right">

                    </div>
                </div>
            </div>
        </section>
    )
}

export default Product;
