import { GlobalContext } from "../context/GlobalState";
import { useContext } from "react";
import { Link } from "react-router-dom";

function Cart() {
    const { removeWebsiteFromFavouritesList, favouritesList } = useContext(GlobalContext)

    return (
        <section className="cart">
            <div className="container">
                <div className="cart-inner inner">
                    <div className="cart-title title">
                        <h1>Корзина</h1>
                    </div>
                    <div className="cart-info info">
                        <p></p>
                    </div>
                    {
                        favouritesList.length > 0 ?
                            (
                                <div className="cart-products-list">
                                    {
                                        favouritesList.map((favouritesCard) => (
                                            <div className="cart-product-card">
                                                <Link className="cart-product-card-left">
                                                    <div className="cart-product-card-img">
                                                        <img src={favouritesCard.images[0].url} alt="" className="cart-product-card-img-default" />
                                                        <img src={favouritesCard.defaultArticle.logoPicture[0].url} alt="" className="cart-product-card-img-onhover" />
                                                    </div>
                                                </Link>
                                                <div className="cart-product-card-right">

                                                </div>
                                            </div>
                                        ))
                                    }
                                </div>
                            )
                            :
                            (
                                <div className="cart-empty">

                                </div>
                            )
                    }
                </div>
            </div>
        </section>
    )
}

export default Cart;
